CREATE TABLE PERSONS (PERSON_ID BIGINT NOT NULL, BIRTHDAY DATETIME, CREATION_DATE DATETIME, FIRTS_NAME VARCHAR(20) NOT NULL, GENDER VARCHAR(20) NOT NULL, LAST_UPDATE DATETIME, SECOND_NAME VARCHAR(20), SECOND_SURNAME VARCHAR(20), SURNAME VARCHAR(20), VERSION BIGINT, PRIMARY KEY (PERSON_ID))
CREATE TABLE PERSON_INFORMATION (INFORMATION_ID BIGINT NOT NULL, CREATION_DATE DATETIME, LAST_UPDATE DATETIME, TYPE VARCHAR(30) NOT NULL, VERSION BIGINT, PERSON_ID BIGINT NOT NULL, PRIMARY KEY (INFORMATION_ID))
CREATE TABLE PROFILES (PROFILE_ID BIGINT NOT NULL, CREATION_DATE DATETIME, LAST_UPDATE DATETIME, NAME VARCHAR(30) NOT NULL, STATE TINYINT(1) default 0 NOT NULL, VERSION BIGINT, PRIMARY KEY (PROFILE_ID))
CREATE TABLE USERS (USER_ID BIGINT NOT NULL, CREATION_DATE DATETIME, LAST_UPDATE DATETIME, PASSWORD VARCHAR(150) NOT NULL, STATE VARCHAR(255) NOT NULL, USER_NAME VARCHAR(50) NOT NULL, VERSION BIGINT, PROFILE_ID BIGINT, PERSON_ID BIGINT, PRIMARY KEY (USER_ID))
CREATE TABLE MENU_OPTIONS (MENU_ID BIGINT NOT NULL, URL VARCHAR(50), ACTIVE TINYINT(1) default 0 NOT NULL, CREATION_DATE DATETIME, DEFAULT_KEY VARCHAR(100) NOT NULL, IS_PARENT TINYINT(1) default 0 NOT NULL, KEY_ VARCHAR(50) NOT NULL, LAST_UPDATE DATETIME, REDIRECT TINYINT(1) default 0 NOT NULL, VERSION BIGINT, PARENT_ID BIGINT, PRIMARY KEY (MENU_ID))
CREATE TABLE PERSON_ADD_INFO (INFORMATION_ID BIGINT, VALUE_ VARCHAR(255) NOT NULL, KEY_ VARCHAR(255) NOT NULL)
CREATE TABLE PROFILE_OPTIONS (PROFILE_ID BIGINT NOT NULL, MENU_ID BIGINT NOT NULL, PRIMARY KEY (PROFILE_ID, MENU_ID))
ALTER TABLE PROFILES ADD CONSTRAINT CNN_UN_PROFILE_NAME UNIQUE (NAME)
ALTER TABLE USERS ADD CONSTRAINT CNN_UN_USER_NAME UNIQUE (USER_NAME)
ALTER TABLE PERSON_INFORMATION ADD CONSTRAINT FK_PERSON_INFORMATION_PERSON_ID FOREIGN KEY (PERSON_ID) REFERENCES PERSONS (PERSON_ID)
ALTER TABLE USERS ADD CONSTRAINT FK_USERS_PROFILE_ID FOREIGN KEY (PROFILE_ID) REFERENCES PROFILES (PROFILE_ID)
ALTER TABLE USERS ADD CONSTRAINT FK_USERS_PERSON_ID FOREIGN KEY (PERSON_ID) REFERENCES PERSONS (PERSON_ID)
ALTER TABLE MENU_OPTIONS ADD CONSTRAINT FK_MENU_OPTIONS_PARENT_ID FOREIGN KEY (PARENT_ID) REFERENCES MENU_OPTIONS (MENU_ID)
ALTER TABLE PERSON_ADD_INFO ADD CONSTRAINT FK_PERSON_ADD_INFO_INFORMATION_ID FOREIGN KEY (INFORMATION_ID) REFERENCES PERSON_INFORMATION (INFORMATION_ID)
ALTER TABLE PROFILE_OPTIONS ADD CONSTRAINT FK_PROFILE_OPTIONS_PROFILE_ID FOREIGN KEY (PROFILE_ID) REFERENCES PROFILES (PROFILE_ID)
ALTER TABLE PROFILE_OPTIONS ADD CONSTRAINT FK_PROFILE_OPTIONS_MENU_ID FOREIGN KEY (MENU_ID) REFERENCES MENU_OPTIONS (MENU_ID)
