CREATE TABLE PROFILES (PROFILE_ID BIGINT NOT NULL, CREATION_DATE DATETIME, LAST_UPDATE DATETIME, NAME VARCHAR(30) NOT NULL, STATE TINYINT(1) default 0 NOT NULL, VERSION BIGINT, PRIMARY KEY (PROFILE_ID))
CREATE TABLE USERS (USER_ID BIGINT NOT NULL, CREATION_DATE DATETIME, LAST_UPDATE DATETIME, PASSWORD VARCHAR(150) NOT NULL, STATE VARCHAR(255) NOT NULL, USER_NAME VARCHAR(50) NOT NULL, EMAIL VARCHAR(50) NOT NULL, VERSION BIGINT, PROFILE_ID BIGINT, PRIMARY KEY (USER_ID))
CREATE TABLE MENU_OPTIONS (MENU_ID BIGINT NOT NULL, ACTIVE TINYINT(1) default 0 NOT NULL, CREATION_DATE DATETIME, DEFAULT_KEY VARCHAR(100) NOT NULL, IS_PARENT TINYINT(1) default 0 NOT NULL, KEY_ VARCHAR(50) NOT NULL, LAST_UPDATE DATETIME, REDIRECT TINYINT(1) default 0 NOT NULL, URL VARCHAR(50), VERSION BIGINT, PARENT_ID BIGINT, PRIMARY KEY (MENU_ID))
CREATE TABLE CENTERS (CENTER_ID BIGINT NOT NULL, CREATION_DATE DATETIME, LAST_UPDATE DATETIME, CENTER_NAME VARCHAR(50) NOT NULL, ACTIVE TINYINT(1) default 0 NOT NULL, VERSION BIGINT, PRIMARY KEY (CENTER_ID))
CREATE TABLE CENTER_AREAS (AREA_ID BIGINT NOT NULL, CREATION_DATE DATETIME, LAST_UPDATE DATETIME, AREA_NAME VARCHAR(50) NOT NULL, STATE TINYINT(1) default 0 NOT NULL, VERSION BIGINT, CENTER_ID BIGINT NOT NULL, PRIMARY KEY (AREA_ID))
CREATE TABLE INTERNALS (INTERNAL_ID BIGINT NOT NULL, BIRTHDAY DATETIME NOT NULL, CREATION_DATE DATETIME, FIRST_NAME VARCHAR(30) NOT NULL, GENDER VARCHAR(15) NOT NULL, INTERNAL_YEAR VARCHAR(15) NOT NULL, LAST_UPDATE DATETIME, SECOND_NAME VARCHAR(30), SECOND_SURNAME VARCHAR(30) NOT NULL, SURNAME VARCHAR(30) NOT NULL, THIRD_NAME VARCHAR(30), VERSION BIGINT, INTERNAL_PARTNER_ID BIGINT, USER_ID BIGINT, PRIMARY KEY (INTERNAL_ID))
CREATE TABLE INTERNAL_INFORMATION (INFORMATION_ID BIGINT NOT NULL, CREATION_DATE DATETIME, LAST_UPDATE DATETIME, TYPE VARCHAR(30) NOT NULL, VERSION BIGINT, PERSON_ID BIGINT NOT NULL, PRIMARY KEY (INFORMATION_ID))
CREATE TABLE PROFILE_OPTIONS (PROFILE_ID BIGINT NOT NULL, MENU_ID BIGINT NOT NULL, PRIMARY KEY (PROFILE_ID, MENU_ID))
CREATE TABLE INTERNAL_ADD_INFO (INFORMATION_ID BIGINT, VALUE_ VARCHAR(255) NOT NULL, KEY_ VARCHAR(255) NOT NULL)
ALTER TABLE PROFILES ADD CONSTRAINT CNN_UN_PROFILE_NAME UNIQUE (NAME)
ALTER TABLE USERS ADD CONSTRAINT CNN_UN_USER_NAME UNIQUE (USER_NAME)
ALTER TABLE USERS ADD CONSTRAINT FK_USERS_PROFILE_ID FOREIGN KEY (PROFILE_ID) REFERENCES PROFILES (PROFILE_ID)
ALTER TABLE MENU_OPTIONS ADD CONSTRAINT FK_MENU_OPTIONS_PARENT_ID FOREIGN KEY (PARENT_ID) REFERENCES MENU_OPTIONS (MENU_ID)
ALTER TABLE CENTER_AREAS ADD CONSTRAINT FK_CENTER_AREAS_CENTER_ID FOREIGN KEY (CENTER_ID) REFERENCES CENTERS (CENTER_ID)
ALTER TABLE INTERNALS ADD CONSTRAINT FK_INTERNALS_USER_ID FOREIGN KEY (USER_ID) REFERENCES USERS (USER_ID)
ALTER TABLE INTERNALS ADD CONSTRAINT FK_INTERNALS_INTERNAL_PARTNER_ID FOREIGN KEY (INTERNAL_PARTNER_ID) REFERENCES INTERNALS (INTERNAL_ID)
ALTER TABLE INTERNAL_INFORMATION ADD CONSTRAINT FK_INTERNAL_INFORMATION_PERSON_ID FOREIGN KEY (PERSON_ID) REFERENCES INTERNALS (INTERNAL_ID)
ALTER TABLE PROFILE_OPTIONS ADD CONSTRAINT FK_PROFILE_OPTIONS_PROFILE_ID FOREIGN KEY (PROFILE_ID) REFERENCES PROFILES (PROFILE_ID)
ALTER TABLE PROFILE_OPTIONS ADD CONSTRAINT FK_PROFILE_OPTIONS_MENU_ID FOREIGN KEY (MENU_ID) REFERENCES MENU_OPTIONS (MENU_ID)
ALTER TABLE INTERNAL_ADD_INFO ADD CONSTRAINT FK_INTERNAL_ADD_INFO_INFORMATION_ID FOREIGN KEY (INFORMATION_ID) REFERENCES INTERNAL_INFORMATION (INFORMATION_ID)
